mapper.insert:
INSERT INTO ${LIKE_REGION_TBL} (
    member_id, sido_cd, ssg_cd, umd_cd, is_like, umd_nm
) VALUES (
     #{memberId}, #{sidoCd}, #{ssgCd}, #{umdCd}, #{isLike}, #{umdNm}
)

mapper.update:
UPDATE ${LIKE_REGION_TBL}
SET
    is_like = #{isLike},
    update_date = NOW()
WHERE
    member_id = #{memberId}
  AND sido_cd = #{sidoCd}
  AND ssg_cd = #{ssgCd}
  AND umd_cd = #{umdCd}

mapper.select:
SELECT member_id, sido_cd,ssg_cd,umd_cd, is_like
FROM ${LIKE_REGION_TBL}
WHERE member_id = #{memberId}
  AND sido_cd = #{sidoCd}
  AND ssg_cd = #{ssgCd}
  AND umd_cd = #{umdCd}

SELECT sido_cd, ssg_cd, umd_cd, umd_nm
FROM ${LIKE_REGION_TBL}
WHERE member_id = #{memberId}
  AND is_like = 1
